(34, 32, '_wp_attachment_metadata', 'a:5:{s:5:\"width\";i:3200;s:6:\"height\";i:1520;s:4:\"file\";s:37:\"2019/03/header-whitetree-improved.png\";s:5:\"sizes\";a:6:{s:9:\"thumbnail\";a:4:{s:4:\"file\";s:37:\"header-whitetree-improved-150x150.png\";s:5:\"width\";i:150;s:6:\"height\";i:150;s:9:\"mime-type\";s:9:\"image/png\";}s:6:\"medium\";a:4:{s:4:\"file\";s:37:\"header-whitetree-improved-300x143.png\";s:5:\"width\";i:300;s:6:\"height\";i:143;s:9:\"mime-type\";s:9:\"image/png\";}s:12:\"medium_large\";a:4:{s:4:\"file\";s:37:\"header-whitetree-improved-768x365.png\";s:5:\"width\";i:768;s:6:\"height\";i:365;s:9:\"mime-type\";s:9:\"image/png\";}s:5:\"large\";a:4:{s:4:\"file\";s:38:\"header-whitetree-improved-1024x486.png\";s:5:\"width\";i:1024;s:6:\"height\";i:486;s:9:\"mime-type\";s:9:\"image/png\";}s:14:\"post_thumbnail\";a:4:{s:4:\"file\";s:37:\"header-whitetree-improved-600x250.png\";s:5:\"width\";i:600;s:6:\"height\";i:250;s:9:\"mime-type\";s:9:\"image/png\";}s:16:\"post_thumbnail_1\";a:4:{s:4:\"file\";s:35:\"header-whitetree-improved-70x70.png\";s:5:\"width\";i:70;s:6:\"height\";i:70;s:9:\"mime-type\";s:9:\"image/png\";}}s:10:\"image_meta\";a:12:{s:8:\"aperture\";s:1:\"0\";s:6:\"credit\";s:0:\"\";s:6:\"camera\";s:0:\"\";s:7:\"caption\";s:0:\"\";s:17:\"created_timestamp\";s:1:\"0\";s:9:\"copyright\";s:0:\"\";s:12:\"focal_length\";s:1:\"0\";s:3:\"iso\";s:1:\"0\";s:13:\"shutter_speed\";s:1:\"0\";s:5:\"title\";s:0:\"\";s:11:\"orientation\";s:1:\"0\";s:8:\"keywords\";a:0:{}}}'),
